mapper.insert:
select seq_schedule.nextval from dual
insert into schedule ( schedule_num, member_id, title, subject, start_date, end_date
) values ( #{schedule_num}, #{member_id}, #{title}, #{subject}, #{start_date}, #{end_date}
)

mapper.update:
update today_schedule set (
	start_time,
	end_time
) values (
	#{start_time},
	#{end_time}
) where subject = #{subject} and today = #{today}
